# Nodes to remove
nodes_to_remove = ['E_1']

# Remove nodes from graph dictionary
for node in nodes_to_remove:
    if node in graph:
        del graph[node]
    
    # Remove edges to these nodes from other nodes
    for key in graph:
        graph[key] = [(neighbor, weight) for neighbor, weight in graph[key] if neighbor not in nodes_to_remove]

# Remove coordinates for the removed nodes
for node in nodes_to_remove:
    if node in coordinates:
        del coordinates[node]

# Print the updated graph (optional)
print("Updated graph after removing nodes:")
print(graph)

# Print the updated coordinates (optional)
print("Updated coordinates after removing nodes:")
print(coordinates)

